gapText.paragraph:
import java.util.Scanner;
public class ReadingExercice {
    public static void main (String[] args) {  
        Scanner keyboard = new Scanner(System.in);  
        int integerNb = keyboard.nextInt();
        double realNb = keyboard.nextDouble() ;
        boolean ok = keyboard.nextBoolean() ;
        String string1 = keyboard.next() ; // We read a word
        String string2 = keyboard.nextLine() ; // We read a line 
        char aCharacter = keyboard.next.charAt() ;
    }
}

