@Test
public void occurence_cas1() {
  int nb = 122555;
  int [] tabOccurencesAttendues = {0,1,2,0,0,3,0,0,0,0};
  int [] tabOccurencesObtenues = MaClasse.occurence(nb);

  // We check that the array exists
  assertTrue(tabOccurencesObtenues!=null);

  // We check that the array size is correct
  assertEquals(tabOccurencesAttendues.length, tabOccurencesObtenues.length);

  // We check that each element is the expected one
  for (int i=0; i<tabOccurencesAttendues.length; i++){
    assertEquals (tabOccurencesAttendues[i], tabOccurencesObtenues[i]);
  }    
}
